mapper.select:
SELECT id, `datetime`, project_id projectId, `group`, `type`, IFNULL(traders, "") traders, IFNULL(tradersTime, "") tradersTime, IFNULL(successor, "") successor, IFNULL(successorTime, "") successorTime, IFNULL(recorder, "") recorder, other, ( SELECT GROUP_CONCAT( c.userName ORDER BY FIND_IN_SET( c.userNumber, REPLACE (s.traders, ';', ',') ) ) FROM users c WHERE FIND_IN_SET( c.userNumber, REPLACE (s.traders, ';', ',') )
) AS tradersName, ( SELECT GROUP_CONCAT( c.userName ORDER BY FIND_IN_SET( c.userNumber, REPLACE (s.successor, ';', ',') ) ) FROM users c WHERE FIND_IN_SET( c.userNumber, REPLACE (s.successor, ';', ',') )
) AS successorName, ( SELECT GROUP_CONCAT( c.userName ORDER BY FIND_IN_SET( c.userNumber, REPLACE (s.recorder, ';', ',') ) ) FROM users c WHERE FIND_IN_SET( c.userNumber, REPLACE (s.recorder, ';', ',') )
) AS recorderName FROM scrdaily s WHERE datetime = #{datetime} AND type = #{type} AND project_id = #{project} AND other = #{other}

mapper.update:
UPDATE scrdailyrecord SET content1 = #{scrDailyRecord.content1},content2 = #{scrDailyRecord.content2},
content3 = #{scrDailyRecord.content3},content4 = #{scrDailyRecord.content4},
content5 = #{scrDailyRecord.content5},content6 = #{scrDailyRecord.content6} 
WHERE id = #{scrDailyRecord.id};
